import React,{ useEffect} from 'react';
import "./Home.css";
import { useNavigate } from "react-router-dom";

export default function Home() {
    const navigate = useNavigate();
    useEffect(() => {
        const token = localStorage.getItem("jwt");
        if(!token){
            navigate("./signup");
        }

        // Fetching all the posts
        fetch("http://localhost:5000/allposts",{
            headers:{
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            }
        })

    }, [navigate])

    return (
            <div className="home">
            {/* card */}
                <div className="card">

                {/* card header */}
                <div className="card-header">
                    <div className="card-pic">
                    <img src="https://media.istockphoto.com/id/1226793777/photo/black-and-white-beautiful-smiling-woman-portrait-isolated-on-white-background.jpg?s=612x612&w=0&k=20&c=93Fs3jBtzZFyRBrT3_JtxGLmtBw8gLg6-7zj1Yr_MII="
                        alt="" />
                    </div>
                    <h5>Dakshya</h5>
                </div>

                {/* card image */}
                <div className="card-image">
                    <img src="https://plus.unsplash.com/premium_photo-1664910219302-7a4c7b97da78?w=400&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MjV8fGdpcmwlMjBlbmpveWluZyUyMHRyZWNraW5nJTIwbmF0dXJlfGVufDB8MXwwfHx8MA%3D%3D"
                    alt="" /> 
                </div>

                {/* card content */}
                <div className="card-content">
                    <span className="material-symbols-outlined">favorite</span>
                    <p>1 Like</p>
                    <p>This is amazing</p>
                </div>

                {/* add Comment */}
                <div className="add-comment">
                    <span className="material-symbols-outlined">mood</span>
                    <input type="text"
                        placeholder="Add a comment"  />
                    <button className="comment">Post</button>
                </div>

            </div>
        </div>
    );
}
