import React,{ useEffect, useState } from 'react';
import "./Home.css";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

export default function Home() {
     var picLink = "https://cdn-icons-png.flaticon.com/128/3177/3177440.png"
    const navigate = useNavigate();
    const [data, setData] = useState([]);
    const [comment, setComment] = useState("");
    const [show, setShow] = useState(false);
    const [item, setItem] = useState([]);

     // Toast functions
    const notifyA = (msg) => toast.error(msg);
    const notifyB = (msg) => toast.success(msg);

    useEffect(() => {
        const token = localStorage.getItem("jwt");
        if(!token){
            navigate("./signup");
        }

        // Fetching all the posts
        fetch("http://localhost:5000/allposts",{
            headers:{
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            },
        }).then(res=>res.json())
        .then(result => setData(result))
        .catch(err => console.log(err))

    }, [navigate]);

    // to show and hide comments
    const toggleComment = (posts) => {
    if (show) {
      setShow(false);
    } else {
      setShow(true);
      setItem(posts);
    }
    };

    const likePost = (id) => {
        fetch("http://localhost:5000/like", {
          method: "put",
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + localStorage.getItem("jwt"),
          },
          body: JSON.stringify({
            postId: id,
          }),
        })
          .then((res) => res.json())
          .then((result) => {
            const newData = data.map((posts) => {
              if (posts._id === result._id) {
                return result;
              } else {
                return posts;
              }
            });
            setData(newData);
            console.log(result);
          });
      };

      const unlikePost = (id) => {
        fetch("http://localhost:5000/unlike", {
          method: "put",
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + localStorage.getItem("jwt"),
          },
          body: JSON.stringify({
            postId: id,
          }),
        })
          .then((res) => res.json())
          .then((result) => {
            const newData = data.map((posts) => {
              if (posts._id === result._id) {
                return result;
              } else {
                return posts;
              }
            });
            setData(newData);
            console.log(result);
          });
      };

    //function to make comments
    const makeComment = (text, id) => {
    fetch("http://localhost:5000/comment", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body: JSON.stringify({
        text: text,
        postId: id,
      }),
    })
      .then((res) => res.json())
      .then((result) => {
        const newData = data.map((posts) => {
          if (posts._id === result._id) {
            return result;
          } else {
            return posts;
          }
        });
        setData(newData);
        setComment("");
        notifyB("Comment posted");
        console.log(result);
      });
  };

    return (
            <div className="home">
            {/* card */}
                {data.map((posts) => {
                    return (
                        <div className="card">

                {/* card header */}
                <div className="card-header">
                    <div className="card-pic">
                    <img
                  src={posts.postedBy.Photo ? posts.postedBy.Photo : picLink}
                  alt=""
                />
              </div>
              <h5>
                <Link to={`/profile/${posts.postedBy._id}`}>
                  {posts.postedBy.name}
                </Link>
              </h5>
                </div>

                {/* card image */}
                <div className="card-image">
                    <img src={posts.photo}
                    alt="" /> 
                </div>

                {/* card content */}
                <div className="card-content">
                    {
                        posts.likes.includes( JSON.parse(localStorage.getItem("user"))._id ) ?
                        ( <span className="material-symbols-outlined material-symbols-outlined-red" onClick={() => {unlikePost(posts._id)}}>favorite</span> )
                        : ( <span className="material-symbols-outlined" onClick={() => {likePost(posts._id)}}>favorite</span> )
                    }
                    
                    <p>{posts.likes.length} Likes</p>
                    <p>{posts.body}</p>
                </div>

                {/* add Comment */}
                <div className="add-comment">
                    <span className="material-symbols-outlined">mood</span>
                    <input type="text"
                        placeholder="Add a comment"
                        value={comment}
                        onChange={(e) => {
                            setComment(e.target.value);
                        }}  />
                    <button className="comment" onClick={() => {
                  makeComment(comment, posts._id);
                }}>Post</button>
                </div>

            </div>
                    )
                })}

            {/* Show Comment */}
            {show && (
        <div className="showComment">
          <div className="container">
            <div className="postPic">
              <img src={item.photo} alt="" />
            </div>
            <div className="details">
              {/* card header */}
              <div
                className="card-header"
                style={{ borderBottom: "1px solid #00000029" }}
              >
                <div className="card-pic">
                  <img
                    src="https://images.unsplash.com/photo-1570295999919-56ceb5ecca61?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8cGVyc29ufGVufDB8MnwwfHw%3D&auto=format&fit=crop&w=500&q=60"
                    alt=""
                  />
                </div>
                <h5>{item.postedBy.name}</h5>
              </div>

              {/* commentSection */}
              <div
                className="comment-section"
                style={{ borderBottom: "1px solid #00000029" }}
              >
                {item.comments.map((comment) => {
                  return (
                    <p className="comm">
                      <span
                        className="commenter"
                        style={{ fontWeight: "bolder" }}
                      >
                        {comment.postedBy.name}{" "}
                      </span>
                      <span className="commentText">{comment.comment}</span>
                    </p>
                  );
                })}
              </div>

              {/* card content */}
              <div className="card-content">
                <p>{item.likes.length} Likes</p>
                <p>{item.body}</p>
              </div>

              {/* add Comment */}
              <div className="add-comment">
                <span className="material-symbols-outlined">mood</span>
                <input
                  type="text"
                  placeholder="Add a comment"
                  value={comment}
                  onChange={(e) => {
                    setComment(e.target.value);
                  }}
                />
                <button
                  className="comment"
                  onClick={() => {
                    makeComment(comment, item._id);
                    toggleComment();
                  }}
                >
                  Post
                </button>
              </div>
            </div>
          </div>
          <div
            className="close-comment"
            onClick={() => {
              toggleComment();
            }}
          >
            <span className="material-symbols-outlined material-symbols-outlined-comment">
              close
            </span>
          </div>
        </div>
      )}
             
        </div>
    );
}
