import React,{ useState, useEffect } from 'react';
import "./Createpost.css";
import { toast } from 'react-toastify';
import { useNavigate } from "react-router-dom";

export default function Createpost() {
  const [body, setBody] = useState("");
  const [image, setImage] = useState("")
  const [url, setUrl] = useState("")
  const navigate = useNavigate()

  //Toast functions
  const notifyA = (msg) => toast.error(msg)
  const notifyB = (msg) => toast.success(msg)

  useEffect(() => { 
    // saving post to mongodb
    if(url){

    fetch("http://localhost:5000/createPost", {
      method:"post",
      headers:{
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      },
      body:JSON.stringify({
        body,
        pic:url
      })
    }).then(res=>res.json())
    .then(data=>{if(data.error){
      notifyA("data.error")
    }else{
      notifyB("Successfully Posted")
      navigate("/")
    }})
    .catch(err => console.log(err))
  }  
  }, [url,body])
  

  // posting image to cloudinary
  const postDetails = () => {
    console.log(body,image)
    const data = new FormData()
    data.append("file",image)
    data.append("upload_preset", "insta-clone")
    data.append("cloud_name","cantacloud6")
    fetch("https://api.cloudinary.com/v1_1/cantacloud6/image/upload",
    {
      method:"post",
      body:data
    }).then(res => res.json())
    .then(data => setUrl(data.url))
    .catch(err => console.log(err))

    // saving post to mongodb
    fetch("http://localhost:5000/createPost",{
      method:"post",
      headers:{
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      },
      body:JSON.stringify({
        body,
        pic:url
      })
    }).then(res=>res.json())
    .then(data=>console.log(data))
    .catch(err => console.log(err))
  }

  const loadfile = (event) => {
      var output = document.getElementById("output");
        output.src = URL.createObjectURL(event.target.files[0]);
        output.onload = function () {
          URL.revokeObjectURL(output.src); // free memory
        };
      };

    return (
        <div className="createPost">
      {/* //header */}
      <div className="post-header">
      <h4 style={{ margin: "3px auto" }}>Create New Post</h4>
        <button id="post-btn" onClick={() => { postDetails() }}> Share </button>
      </div>

      {/* image preview */}
      <div className="main-div">
        <img id="output" src="data:image/png;base64,"
        alt="" />
        <input type="file" accept="image/*" 
        onChange={(event) => {
          loadfile(event);
          setImage(event.target.files[0])
        }}  />
        </div>

        {/* details */}
        <div className="details">
            <div className="card-header">
                <div className="card-pic">
                    <img src="https://media.istockphoto.com/id/1226793777/photo/black-and-white-beautiful-smiling-woman-portrait-isolated-on-white-background.jpg?s=612x612&w=0&k=20&c=93Fs3jBtzZFyRBrT3_JtxGLmtBw8gLg6-7zj1Yr_MII=" 
                    alt="" />  
                </div>
                <h5>Dakshya</h5>
            </div>
            <textarea value={body} onChange={(e) => {
              setBody(e.target.value)
            }} type="text" placeholder="Write a caption...."></textarea>
        </div>
      </div>
    );
}
