import React, { useEffect, useState } from 'react';
import PostDetail from "./PostDetail";
import "./Profile.css";

export default function Profile() {
    const [pic, setPic] = useState([])
    const [show, setShow] = useState(false)
    const [posts, setPosts] = useState([]);
    const [user, setUser] = useState("")

    const toggleDetails = (posts) => {
        if (show) {
          setShow(false);
        } else {
          setShow(true);
          setPosts(posts);
        }
      };

    useEffect(() => {
        fetch("http://localhost:5000/myposts",{
            headers:{
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
        })
        .then(res=>res.json())
        .then((result) => {
            // Ensure that result is an array before setting it to pic state
            if (Array.isArray(result)) {
                setPic(result);
            } else {
                console.error("Expected an array, but received:", result);
                setPic([]); // Fallback to an empty array
            }
        })
        .catch((error) => {
            console.error("Failed to fetch posts:", error);
        });
    }, [])

    return (
    <div className="profile">
        {/* Profile frame */}
        <div className="profile-frame">
            {/* profile-pic */}
            <div className="profile-pic">
                <img src="https://images.unsplash.com/photo-1570295999919-56ceb5ecca61?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8cGVyc29ufGVufDB8MnwwfHw%3D&auto=format&fit=crop&w=500&q=60"
                    alt=" " />
            </div>
            {/* profile-data */}
            <div className="pofile-data">
                <h1>{JSON.parse(localStorage.getItem("user")).name}</h1>
                <div className="profile-info" style={{ display:"flex" }}>
                    <p>40 posts</p>
                    <p>40 followers</p>
                    <p>40 following</p>
                </div>
            </div>
        </div>

    <hr style={{
        width:"90%",
        opacity:"0.8",
        margin:"auto"
    }} />

        {/* Gallery */}
        <div className="gallery">
            {Array.isArray(pic) && pic.map((pics)=>{
                return <img key={pics._id} src={pics.photo}  
                alt={pics.description || "Gallery image"} 
                onClick
                className="item"></img>;
            })}
        </div>
        <PostDetail />
    </div>
    );
}
