import React,{ useEffect, useState } from 'react';
import "./Home.css";
import { useNavigate } from "react-router-dom";

export default function Home() {
    const navigate = useNavigate();
    const [data, setData] = useState([]);

    useEffect(() => {
        const token = localStorage.getItem("jwt");
        if(!token){
            navigate("./signup");
        }

        // Fetching all the posts
        fetch("http://localhost:5000/allposts",{
            headers:{
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            },
        }).then(res=>res.json())
        .then(result => setData(result))
        .catch(err => console.log(err))

    }, [navigate]);

    const likePost = (id) => {
        fetch("http://localhost:5000/like")
    }

    return (
            <div className="home">
            {/* card */}
                {data.map((posts) => {
                    return (
                        <div className="card">

                {/* card header */}
                <div className="card-header">
                    <div className="card-pic">
                    <img src="https://media.istockphoto.com/id/1226793777/photo/black-and-white-beautiful-smiling-woman-portrait-isolated-on-white-background.jpg?s=612x612&w=0&k=20&c=93Fs3jBtzZFyRBrT3_JtxGLmtBw8gLg6-7zj1Yr_MII="
                        alt="" />
                    </div>
                    <h5>{posts.postedBy.name}</h5>
                </div>

                {/* card image */}
                <div className="card-image">
                    <img src={posts.photo}
                    alt="" /> 
                </div>

                {/* card content */}
                <div className="card-content">
                    <span className="material-symbols-outlined">favorite</span>
                    <span className="material-symbols-outlined material-symbols-outlined-red">favorite</span>
                    <p>1 Like</p>
                    <p>{posts.body}</p>
                </div>

                {/* add Comment */}
                <div className="add-comment">
                    <span className="material-symbols-outlined">mood</span>
                    <input type="text"
                        placeholder="Add a comment"  />
                    <button className="comment">Post</button>
                </div>

            </div>
                    )
                })}
                
        </div>
    );
}
